import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        languages : [
            {name: "Php", votes: 0},
            {name: "Python", votes: 0},
            {name: "JavaSript", votes: 0},
            {name: "Java", votes: 0}
        ]
    }

    }
  render() {
      return (
          <div> 
              this.state.languages.map((lang, i)=>{

                // voteCount=()=>{
                //   setState(languages[i]('votes'), +1)
                //  }
                <div key = {i}>
                  <span>{lang.votes}</span>
                  <span>{lang.name}</span>
                  <span>
                    <button 
                    // onClick={this.voteCount}
                    onClick={this.setState(lang[i]('votes'), +1)}
                    > 
                    click me
                    </button>
                  </span>
                </div>
            })



          </div>

      )
  }
}



export default App;
