// Ex4

import React from 'react';
class Colors extends React.Component {
    constructor() {
        super();
        this.state ={
            favColor: "red",
        };
      }



    render() {

        return (<p>My favourite color is <span style={{fontStyle: 'italic'}}>{this.state.favColor}</span></p>)

    }
    changeColor = () => {
        this.setState({favColor: 'yellow'})
    };
    componentDidMount() {

        setTimeout(changeColor, 5000);
        }
}

export default Colors;
