import React from 'react';
import './App.css';

class App extends React.Component{
  constructor (){
    super();
    this.state = {
      input1:'',
      input2:''
    };
  }

  handleInput = (event, id) => {
    this.setState({id: event.target.value})
  }




  render (){

    return (
      <div>
        <h1>Adding 2 numbers</h1>
        <input id='input1' type = "number" onChange={this.handleInput.bind(this, id)}/>
        <input id='input2' type = "number" onChange={this.handleInput}/>
        <button onClick={this.handleInput}>Add them</button>
      </div>


    )
  }



}

export default App;
