import React from 'react';
import './App.css';

class App extends React.Component{
  constructor (){
    super();
    this.state = {
      input1:'',
      input2:'',
      total:''
    };
  }

  handleInput1 = (event) => {
    this.setState({input1: event.target.value})
  }

  handleInput2 = (event) => {
    this.setState({input2: event.target.value})
  }

  add = () => {
    this.setState({total: this.state.input1 + this.state.input2})
  }



  render (){

    return (
      <div>
        <h1>Adding 2 numbers</h1>
        <input id='input1' type = "number" onChange={this.handleInput1}/>
        <input id='input2' type = "number" onChange={this.handleInput2}/>
        <button onClick={this.add}>Add them</button>
        <div>{this.state.total}</div>
      </div>


    )
  }



}

export default App;
