import React from 'react';
import './App.css';
import quotes from './QuotesDatabase'

let prevQuote=0;

  class App extends React.Component{
    constructor (){
      super();
      this.state = {
        quote:quotes[0].quote,
        autor:quotes[0].author
      };
    }
    getQuote =()=> {
      let i = Math.floor(Math.random() * quotes.length);
      if(i !== prevQuote) {
        let quote = quotes[i];
        prevQuote = i;
        this.setState({quote:quote.quote})
        this.setState({quote:quote.author})
      } else {
        getQuote()
      }

    }

    render (){
      return (
        <div>
          <h1>{this.state.quote}</h1>
          <p>{this.state.autor}</p>
          <button onClick={this.getQuote}>New quote</button> 

        </div>


      );
    }



  }

export default App;
