import React from 'react';
class Counter extends React.Component{
    constructor (){
      super();
      this.state = {
        count:0
      };
    }

    incrCount = () => {
        count++
    }

    render (){

      return (
        <div className='App'>
         <div>
             {this.state.count}
         </div>
         <button onClick={this.incrCount}></button>
        </div>


      )
    }



  }

  export default Counter;
