import React from 'react';
import './App.css';

class App extends React.Component{
  constructor (){
    super();
    this.state = {
      fName: '',
      lName: '',
      email:'',
      selectedValue:'',
      textArea: '',
      isGoing: true
    };
  }
  handleChange =(e)=> {
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value
    console.log(e.target.name, e.target.value);
    this.setState({[e.target.name]:value})
  }
  // [e.target.name] we don;t need another function than, since now we use  dynamic key in the object, you have to add statename to match input name

  // handleChangeLName =(e)=> {
  //   this.setState({lName:e.target.value})
  // }
  handleSubmit = (e) => {
    e.preventDefault();
  }
  render (){

    return (
      <div className='App'>
        <form onSubmit={this.handleSubmit}>
            <input type="text" placeholder='first name' name = 'fName' onChange ={(e)=>this.handleChange(e)}/> 
            <p>First Name: {this.state.fName}</p>
            <input type="text" placeholder='last name' name = 'lName' onChange ={(e)=>this.handleChange(e)}/> 
            <p>Last Name: {this.state.lName}</p>
            <input type="text" placeholder='email' name = 'email' onChange ={(e)=>this.handleChange(e)}/> 
            <p>Email: {this.state.email}</p>
            <select name ='selectedValue' onChange ={(e)=>this.handleChange(e)}>
              <option value='1'>One</option>
              <option value='2'>Two</option>
              <option value='3'>Three</option>
            </select>
            <p>Select: {this.state.selectedValue}</p>
            <textarea name='textArea' onChange ={(e)=>this.handleChange(e)}></textarea>
            <p>Your text: {this.state.textArea}</p>
            <input type='checkbox' name ='isGoing' onChange ={(e)=>this.handleChange(e)}/>
            <p>Is going: {this.state.isGoing.toString()}</p>
        </form>

      </div>


    )
  }



}

export default App;
