import {useState, useEffect} from 'react'

const Shop2 = (props)=>{
    const [products, setProducts] = useState([])


    useEffect(()=>{
        fetch('http://localhost:5001/api/products/all')
        .then(resp=>resp.json())
        .then(data =>
            {setProducts(data)})

      },[])

      return (
        <div>
            <h1>Shop2</h1>
            {
                products.map((item)=>{
                    return(
                        <div key={item.id}>
                            <h2>{item.name}</h2>
                            <p>{item.price}</p>     
                        </div>
                    ) 
                })
            }
        </div>

    )
}


export default Shop2
