// import logo from './logo.svg';
import './App.css';
import ErrorBoundary from './ErrorBoundary';
import { BrowserRouter, Routes, Route, NavLink } from "react-router-dom";
// import "bootstrap/dist/css/bootstrap.min.css";
import React from 'react';

const HomeScreen =(props)=>{
  return(
    <div>Home</div>
  )
}
const ProfileScreen =(props)=>{
  return(
    <div>ProfileScreen</div>
  )
}


class ShopScreen extends React.Component{
    constructor (){
      super();
      this.state = {
        shop: '',
      };
    }



    render (){
      if(this.state.shop.length < 5) {
          throw Error (`crushed`)
      }
      return (
        <div>ShopScreen</div>


      )
    }



  }


const App =()=> (
  <BrowserRouter>
  <div>
    <nav className='nav nav-pills'>
      <ErrorBoundary>
        <NavLink to="/">
          Home
        </NavLink>
      </ErrorBoundary>
      <ErrorBoundary>
        <NavLink to="/profile">
          Profile
        </NavLink>
      </ErrorBoundary>
      <ErrorBoundary>
        <NavLink to="/shop">
          Shop
        </NavLink>
      </ErrorBoundary>  
    </nav>

      <Routes>
        <ErrorBoundary>
          <Route exact path="/" element={<HomeScreen />} />
        </ErrorBoundary>
        <ErrorBoundary>
          <Route path="/profile" element={<ProfileScreen />} />
        </ErrorBoundary>
        <ErrorBoundary>
          <Route path="/shop" element={<ShopScreen />} />
        </ErrorBoundary>      
    </Routes>
  </div>

  </BrowserRouter>
)


export default App;
