import './App.css';
import {useState, useEffect} from 'react'


function App() {
  const [taskList, addTask] = useState([])
  return (
    <div className="App">
      <header className="App-header">
        <h1>To do</h1>
        <input type='text' placeholder='add task' onSubmit={(e)=>{addTask(e.target.value)}}></input>
            {
                taskList.map((item,i)=>{
                    return(
                        <div key={i}>
                            <p>{item}</p>     
                        </div>
                    ) 
                })
            }
      </header>
    </div>
  );
}

export default App;
