const initState={
    count:0,
}

export const reducer = (state=initState, action={}) =>{
    switch (action.type) {
        case 'PLUS':
            return {...state, count:count + 1}
        case 'MINUS':
            return {...state, count:count - 1}
        default:
            return {...state}
    }

}
