import {connect} from 'react-redux';

const Counter = (props) => {
    return (
        <div>
            <button>+</button>
            <p>{props.c}</p>
            <button>-</button>
        </div>
    )
}

const mapStateToProps = (state)=> {
    return {
        c:state.count
    }
}

export default  connect(mapStateToProps) (Counter)
