import {submit, edit, del} from './actions'

const initState = {
    transactionList: [
    ],
    transaction:{}
}

export const reducer = (state=initState, action={}) => {
    switch (action.type) {
        case 'SUBMIT':
        return  {...state, transactionList: transactionList.push(action.payload), transaction: action.payload}

        case 'EDIT':
        return  {...state, transactionList: transactionList.push(action.payload), transaction: action.payload}

        case 'DELETE' :
        return  {...state, transactionList: transactionList.delete(action.payload)}

    default:
    return {...state}
    }
}
