import { INSERT
 } from "./actions";
const initState ={
    curIndex: -1,
    list:[]
}


export const reducer = (state=initState, action={})=> {
    switch(action.type){
        case INSERT:
            state.list.push(action.payload)
            return {...state, list: [...state.list]}
        default:
            return {...state};
    }


}
