import {useState, useEffect} from 'react';
import {connect} from 'react-redux';
import {insertTrx} from '../redux/actions';

const TransactionForm =(props)=> {
    const [transaction, setTransaction] = useState({
        accountNum:'',
        fsc:'',
        name:'',
        amount:''
    })
    const handleSubmit = (e) => {
        e.preventDefault();
        props.handleInsert(transaction)
        setTransaction({
            accountNum:'',
            fsc:'',
            name:'',
            amount:''
        })

    }
    const handleChange = (e) => {
        setTransaction({...transaction, [e.target.name]:e.target.value})
    }

    return (
        <>
        <h1>Transaction Form</h1>
        <form onSubmit={handleSubmit}>
            <input type='text'
                   name='accountNum'
                    onChange={handleChange}
                    placeholder = 'accountNum'
                    value={transaction.accountNum}
            /><br/>
            <input type='text'
                   name='fsc'
                    onChange={handleChange}
                    placeholder = 'fsc'
                    value={transaction.fsc}
            /><br/>
            <input type='text'
                   name='name'
                    onChange={handleChange}
                    placeholder = 'name'
                    value={transaction.name}
            /><br/>
            <input type='text'
                   name='amount'
                    onChange={handleChange}
                    placeholder = 'amount'
                    value={transaction.amount}

            /><br/>
            <input type='submit' value={props.curInd === -1 ? 'Submit': 'Update'}/>
        </form>
        </>
    )
}

const mapStateToProps = (state)=> {

    return {
        curInd: state.curIndex
    }
};
const mapDispatchToProps = (dispatch) => {
    return {
        handleInsert: (trx) => dispatch(insertTrx(trx))
    }
}

export default connect (mapStateToProps, mapDispatchToProps)(TransactionForm)
