import { INSERT, UPT_IND} from "./actions";


const initState ={
    curIndex: -1,
    list:[]
}


export const reducer = (state=initState, action={})=> {
    switch(action.type){
        case INSERT:
            state.list.push(action.payload)
            return {...state, list: [...state.list], curIndex: -1};
        case UPT_IND:
            return {...state, curIndex:action.payload};
        default:
            return {...state};
    }


}
