import { searchSubmit, searchInput } from "./action"

const initState = {
    imageList: [],
    searchInput:'',
}


export const reducer = (state=initState, action={}) => {
    switch (action.type) {
        case 'SUBMIT':
            const options = {
                method: 'GET',
                headers: {
                  Authorization: '563492ad6f91700001000001099cb8888c044472af40dc3b60a997e1',
                  'X-RapidAPI-Key': '4980814c76msh916d21acc0ea2b9p1680a1jsn3be7217effc7',
                  'X-RapidAPI-Host': 'PexelsdimasV1.p.rapidapi.com'
                }
              };

              fetch(`https://pexelsdimasv1.p.rapidapi.com/v1/search?query=${state.searchInput}&locale=en-US&per_page=30&page=1&Max=30`, options)
                .then(response => response.json())
                .then((response) => {
                  console.log(response)
                  let newArr = response.photos;
                  return {...state, imageList: newArr}
                })
                .catch(err => console.error(err))


        case 'INPUT':
            console.log(state.searchInput)
        return  {...state, searchInput: action.payload}


        default:
        return {...state}
    }
}
