const initState={
    num:0,
    robots:[]
}

export const reducer = (state = initState, action={}) => {


    switch (action.type) {
        case 'PLUS':
            return {...state, num: state.num + 1}

        case 'MINUS':
            return {...state, num: state.num - 1}

            case 'GET':
            return {...state, robots: payload}

        default:
            return {...state}
    }
}
