import './App.css';
import {Provider} from 'react-redux';
import {createStore, applyMiddleware} from 'redux';

import {reducer} from './redux/reducer';
// import myMiddleWare from './redux/middlewares';
import thunk from 'redux-thunk';



import CardList from "./components/CardList";
import SearchBox from "./components/SearchBox";
import Scroll from "./components/Scroll";
import ErrorBoundary from "./components/ErrorBoundary";
import {connect} from 'react-redux'
import {searchFieldChange, getRobots} from './redux/action'

const store = createStore(reducer, applyMiddleware(thunk));

const  App = (props)=> {
console.log(props.robots)
console.log(props.searchField)
const filterRobots = props.robots.filter(
      (robot) => {
        return robot.name
          .toLowerCase()
          .includes(props.searchField.toLowerCase());
      }
    );
  return (
    <Provider store={store}>
        <div className="App-header" onLoad={props.getRobots}>
        <div className="tc">
          <h1 className="f1">RoboFriends</h1>
          <SearchBox
            searchChange={props.searchFieldChng}
          />

          <Scroll>
            <ErrorBoundary>
              <CardList robots={props.robots} />
            </ErrorBoundary>
          </Scroll>
        </div>
    </div>
    </Provider>

      );


  }

//add onloading listener to cardlist add Err msg & loading components


const mapStateToProps = (state) => {
  return {
      robots: state.robots,
      searchField: state.searchField || ''

  }
}

const mapDispatchToProps = (dispatch)=> {
  return {
    searchFieldChng: (txt) => dispatch(searchFieldChange(txt)),
    getRobots: ()=>dispatch(getRobots())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
