export const plus = () => {
    return {
        type:'PLUS'
    }

}

export const minus = () => {
    return {
        type:'MINUS'
    }

}

export const incrIfOdd = ()=> {
    return dispatch => {
        setTimeout(()=>{
            dispatch(plus())
        }, 3000
        )
    }
}
