export const plus = () => {
    return {
        type:'PLUS'
    }

}

export const minus = () => {
    return {
        type:'MINUS'
    }

}

export const incrIfOdd = ()=> {
    return dispatch => {
        if(state.num%2==0){
            dispatch(plus())
        }
    }
}

export const incDelay = ()=> {
    return dispatch => {
        setTimeout(()=>{
            dispatch(plus())
        }, 1000
        )
    }
}
