const Post = (props)=> {
    return (
        <div key = {props.post.id}>
            <h2>{props.post.title}</h2>
            <p>{props.post.body}</p>
            <button>delete</button>
        </div>
    )
}

const mapStateToProps = (state, ownProps) => {
    let id = ownProps.match.params.id
    return {
        post: state.posts.find(post => post.id === id)
    }
  }

