import {Link} from "react-router-dom";


const Home = ({posts}) => {
    if(posts.length<1){
        return(
            <>
            <h1>No posts to show</h1>
            </>
        )
    } else {
        return (
            <div className="post card">
            {
                posts.map((item)=>{
                    return (
                        <div key={item.id} className="card-content">
                            <img src="./blog.png"/>
                            <Link to={'./post/' + item.id}>
                                <h2 className="card-title red-text">{item.title}</h2>
                            </Link>
                                <p>{item.body}</p>
                        </div>
                    )
                })
            }
            </div>
        )
    }

}

export default Home
