const initState = {
    input:'',
    movieList:[],
    movieData:{},
    link:'https://www.omdbapi.com/?apikey=addfb034&s=',
    movieId:'',
    movieLink:'https://www.omdbapi.com/?apikey=addfb034&i=',
    err:''
}

export const reducer = (state=initState, action={})=>{
    switch (action.type) {
        case 'SEARCH':
            return {...state, input: action.payload, link: state.link.concat(action.payload)}
        case 'GET_SUCCESS':
            return {...state, movieList: action.payload}
        case 'GET_FAIL':
            return {...state, err: action.payload}

        default:
            return {...state}
    }
}
