const initState = {
    input:'',
    movieList:[],
    movieData:{},
    link:'https://www.omdbapi.com/?apikey=addfb034&s=',
    movieId:'',
    movieLink:'https://www.omdbapi.com/?apikey=addfb034&i='
}

export const reducer = (state=initState, action={})=>{
    switch (action.type) {
        case 'SEARCH':
            return {...state, input: action.payload}

        default:
            return {...state}
    }
}
