const initState = {
    input:'',
    movieList:[],
    movieData:{}
}

export const reducer = (state=initState,action={})=>{
    switch (action.type) {
        case 'SEARCH':
            return {...state, input: action.payload}

        default:
            return {...state}
    }
}
