import {connect} from 'react-redux'
const Search =(props) => {
    return (
        <>
            <h1> Search for a movie ,TV series ..
</h1>
            <input type="text" onChange={props.searchFieldChange(e.target.value)}/>
            <button onClick={props.getMovies}>Search</button>
        </>

    )

}

const mapStateToProps = (state) => {
    return {
        input: state.input || ''

    }
  }

  const mapDispatchToProps = (dispatch)=> {
    return {
      searchFieldChng: (txt) => dispatch(searchFieldChange(txt)),
      getMovies: ()=>(getMovies(dispatch))
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(Search)
